using System.Collections.Generic;
using System.ComponentModel.DataAnnotations;
using System.Text;
using System.Text.Json.Serialization;

namespace MovieSite.Domain.Models
{
    public class Movie
    {
        [Key]
        public int Id { get; set; }
        [MaxLength((256))]
        public string Title { get; set; }
        [MaxLength(1024)]
        public string Description { get; set; }
        public byte[] Cover { get; set; } = Encoding.UTF8.GetBytes(BaseCover);
        [MaxLength(128)]
        public string YoutubeLink { get; set; }
        [JsonIgnore]
        public virtual IList<GenreMovie> GenreMovies { get; set; }
        [JsonIgnore]
        public virtual IList<MovieRating> MovieRatings { get; set; }
        [JsonIgnore]
        public virtual ICollection<Comment> Comments { get; set; }
        
        [JsonIgnore]
        private const string BaseCover = "data:image/png;base64,";
    }
}